// src/components/CadastroCliente/CadastroCliente.js

import React, { useState } from 'react';

const CadastroCliente = () => {
    const [nome, setNome] = useState('');
    const [sexo, setSexo] = useState('M');
    const [idade, setIdade] = useState('');
    const [nascimento, setNascimento] = useState('');
    const [pontos, setPontos] = useState(0);

    const handleSubmit = (e) => {
        e.preventDefault();
        // Aqui você deve adicionar a lógica para enviar os dados para o banco de dados
        console.log({ nome, sexo, idade, nascimento, pontos });
    };

    return (
        <form onSubmit={handleSubmit}>
            <input type="text" placeholder="Nome" value={nome} onChange={(e) => setNome(e.target.value)} required />
            <select value={sexo} onChange={(e) => setSexo(e.target.value)} required>
                <option value="M">Masculino</option>
                <option value="F">Feminino</option>
                <option value="O">Outros</option>
            </select>
            <input type="number" placeholder="Idade" value={idade} onChange={(e) => setIdade(e.target.value)} required />
            <input type="date" value={nascimento} onChange={(e) => setNascimento(e.target.value)} required />
            <input type="number" placeholder="Pontos" value={pontos} onChange={(e) => setPontos(e.target.value)} required />
            <button type="submit">Cadastrar Cliente</button>
        </form>
    );
};

export default CadastroCliente;
