// components/DishForm.js
import React, { useState } from 'react';
import styles from './DishForm.module.css';

function DishForm() {
    const [dishName, setDishName] = useState('');
    const [description, setDescription] = useState('');
    const [value, setValue] = useState('');
    const [available, setAvailable] = useState(true);

    const handleSubmit = (e) => {
        e.preventDefault();
        // Aqui você pode adicionar a lógica para enviar os dados para o backend
        console.log({ dishName, description, value, available });
        // Resetar o formulário
        setDishName('');
        setDescription('');
        setValue('');
        setAvailable(true);
    };

    return (
        <div className={styles.formContainer}>
            <h2 className={styles.title}>Cadastrar Prato</h2>
            <form onSubmit={handleSubmit}>
                <div className={styles.formGroup}>
                    <label htmlFor="dishName">Nome do Prato:</label>
                    <input
                        id="dishName"
                        type="text"
                        value={dishName}
                        onChange={(e) => setDishName(e.target.value)}
                        className={styles.inputField}
                        required
                    />
                </div>
                <div className={styles.formGroup}>
                    <label htmlFor="description">Descrição:</label>
                    <input
                        id="description"
                        type="text"
                        value={description}
                        onChange={(e) => setDescription(e.target.value)}
                        className={styles.inputField}
                    />
                </div>
                <div className={styles.formGroup}>
                    <label htmlFor="value">Valor:</label>
                    <input
                        id="value"
                        type="number"
                        value={value}
                        onChange={(e) => setValue(e.target.value)}
                        className={styles.inputField}
                        required
                    />
                </div>
                <div className={styles.formGroup}>
                    <label htmlFor="available">Disponível:</label>
                    <input
                        id="available"
                        type="checkbox"
                        checked={available}
                        onChange={(e) => setAvailable(e.target.checked)}
                    />
                </div>
                <button type="submit" className={styles.submitButton}>Cadastrar Prato</button>
            </form>
        </div>
    );
}

export default DishForm;
