// src/App.js
import React, { useState } from 'react';
import Login from './components/Login';
import Dashboard from './components/Dashboard';
import CadastroCliente from './components/CadastroCliente';
import GerenciarPratos from './components/GerenciarPratos';
import RealizarVenda from './components/RealizarVenda';

const App = () => {
    const [user, setUser] = useState(null);
    const [clientes, setClientes] = useState([]);
    const [pratos, setPratos] = useState([
        { id: 1, nome: 'Marguerita' },
        { id: 2, nome: 'Chocolate' },
        // Adicione os outros pratos aqui...
    ]);

    const handleLogin = (userData) => {
        setUser(userData);
    };

    const handleAddCliente = (cliente) => {
        setClientes([...clientes, { id: clientes.length + 1, ...cliente }]);
    };

    const handleAddPrato = (prato) => {
        setPratos([...pratos, { id: pratos.length + 1, ...prato }]);
    };

    const handleVenda = (venda) => {
        // Adicione a lógica para registrar a venda
        console.log('Venda realizada:', venda);
    };

    return (
        <div>
            {!user ? (
                <Login onLogin={handleLogin} />
            ) : (
                <Dashboard user={user} />
            )}
            <CadastroCliente onAddCliente={handleAddCliente} />
            <GerenciarPratos onAddPrato={handleAddPrato} />
            <RealizarVenda pratos={pratos} onVenda={handleVenda} />
        </div>
    );
};

export default App;
